import { FormGroup, ValidatorFn } from "@angular/forms";


export function matchPasswordsValidator(passOne: string,passTwo: string,):ValidatorFn{
    return(control) =>{
        const group = control as FormGroup;
        const passCtrl1 = group.get(passOne)
        const passCtrl2 = group.get(passTwo)

        return passCtrl1?.value === passCtrl2?.value 
        ? null 
        : {matchPasswordsValidator: true}
    }
}
