import { ValidatorFn } from "@angular/forms";

export function appEmailValidator(emails:string[],domains:string[]):ValidatorFn {

    const domainStrings = domains.join("|");
    const emailStrings = emails.join("|");
    const regExp = new RegExp(`[^@]{5,}@${emailStrings}\.(${domainStrings})$`)

    return (control)=>{
        return control.value === '' || regExp.test(control.value)
        ? null 
        : {appEmailValidator:true}
    }
}
