import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from '../api.service';
import { UserService } from '../user/user.service';

@Component({
  selector: 'app-edit-food',
  templateUrl: './edit-food.component.html',
  styleUrls: ['./edit-food.component.scss']
})
export class EditFoodComponent implements OnInit{

  id:string = ''
  food:string = ''

  constructor(private router:Router,
     private activatedRoute:ActivatedRoute,
     private apiService:ApiService,
     private userService:UserService){}

ngOnInit(): void {
  this.id = this.activatedRoute.snapshot.params['foodId'];
  this.apiService.getOne(this.id).subscribe((food)=>{
    this.food = food.type  
  })
}


  editFood(form:NgForm):void{
    const {type} = form.value;

    this.apiService.updateOne(this.id, type).subscribe(()=>{
      this.router.navigate(['/catalog'])
    })

  }

  cancel():void{
    this.router.navigate(['/catalog'])
  }
}
<div class="container">
    <form class="login" #form="ngForm" (submit)="editFood(form)">

        <fieldset>
            <h1>Edit Page</h1>
            <h2> Do you want to change this meal?</h2>

            <p class="field field-icon">
                <input class="input-error" type="text" name="type" id="type" value={{food}}
                ngModel #inputFood="ngModel" required minlength="4">
            </p>

            <ng-container *ngIf="inputFood.touched">
                <p class="error" *ngIf="inputFood.errors?.['required']">Input is required!</p>
                <p class="error" *ngIf="inputFood.errors?.['minlength']">
                    Meal must be at least 4 characters long.
                </p>
            </ng-container>

            <!-- <div>
                <button [disabled]="form.invalid">Edit</button>
                <button >Cancel</button>
            </div> -->
            <div class="columns">
                <div class="buttons">
                    <button >Edit</button>
                </div>
                <div class="buttons">
                    <button (click)="cancel()">Cancel</button>
                </div>
            </div>

        </fieldset>
    </form>

</div>
