import { Component } from '@angular/core';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';

@Component({
  selector: 'app-emoji-display',
  templateUrl: './emoji-display.component.html',
  styleUrls: ['./emoji-display.component.scss'],
//   template: `
//   <div [innerHTML]="emojiHTML"></div>
// `
})

export class EmojiDisplayComponent {
  emojiHTML: SafeHtml;

  constructor(private domSanitizer: DomSanitizer) {
    // Get the HTML representation of the emoji and sanitize it
    const emojiUnicode = '1F604'; // Replace this with the Unicode code point of your desired emoji
    const emojiHTML = `&#x${emojiUnicode};`;
    this.emojiHTML = this.domSanitizer.bypassSecurityTrustHtml(emojiHTML);
  }
}
