import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-edit-food',
  templateUrl: './edit-food.component.html',
  styleUrls: ['./edit-food.component.scss']
})
export class EditFoodComponent {


  editFood(form:NgForm):void{

  }
}
<div class="container">
    <form class="login" #form="ngForm" (submit)="editFood(form)">

        <fieldset>
            <h2>Edit Page</h2>
            <h3> he might be willing to try?</h3>

            <p class="field field-icon">
                <input class="input-error" type="text" name="type" id="type" placeholder="Beef and Tomato"
                ngModel #inputFood="ngModel" required minlength="4">
            </p>

            <ng-container *ngIf="inputFood.touched">
                <p class="error" *ngIf="inputFood.errors?.['required']">Flavour is required!</p>
                <p class="error" *ngIf="inputFood.errors?.['minlength']">
                    Meal must be at least 4 characters long.
                </p>
            </ng-container>

            <button [disabled]="form.invalid">Add to Menu</button>

        </fieldset>
    </form>

</div>
