import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ApiService } from '../api.service';
import { Router } from '@angular/router';
import { Food } from 'src/types/food';
import { UserService } from '../user/user.service';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.scss']
})
export class CreateComponent implements OnInit{
  foodList: Food[] = [];
  isExisting: boolean = false;

ngOnInit(): void {
  if (localStorage.getItem('[user]')) {
    this.userService.loggedIn = true;
  }
}
constructor(private apiService:ApiService, private router:Router, private userService:UserService){}

  addFoodSubmitHandler(form:NgForm):void{
    if(form.invalid)return;

    const {type} = form.value;

      this.apiService.addFood(type).subscribe(()=>{
        this.router.navigate(['/catalog'])
      },
      (error) => {
        console.error('Error fetching data:', error);
      })

  }

}
<div class="container">
    <form class="login" #form="ngForm" (submit)="addFoodSubmitHandler(form)">

        <fieldset>
            <h3>Do you know any flavours </h3>
            <h3> he might be willing to try?</h3>

            <p class="field field-icon">
                <input class="input-error" type="text" name="type" id="type" placeholder="Beef and Tomato"
                ngModel #inputFood="ngModel" required minlength="4">
            </p>

            <ng-container *ngIf="inputFood.touched">
                <p class="error" *ngIf="inputFood.errors?.['required']">Flavour is required!</p>
                <p class="error" *ngIf="inputFood.errors?.['minlength']">
                    Meal must be at least 4 characters long.
                </p>
            </ng-container>

            <button [disabled]="form.invalid">Add to Menu</button>

        </fieldset>
    </form>

</div>
